import React, { useState } from 'react';

function Curriculo() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [experiencia, setExperiencia] = useState('');
  const [formacao, setFormacao] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Currículo enviado: \nNome: ${nome}\nE-mail: ${email}\nExperiência: ${experiencia}\nFormação: ${formacao}`);
  };

  return (
    <div id="curriculo" className="curriculo-container">
      <h2>Cadastro de Currículo</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label>Nome Completo:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            placeholder="Digite seu nome completo"
          />
        </div>

        <div className="input-group">
          <label>E-mail:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Digite seu e-mail"
          />
        </div>

        <div className="input-group">
          <label>Experiência Profissional:</label>
          <textarea
            value={experiencia}
            onChange={(e) => setExperiencia(e.target.value)}
            required
            placeholder="Descreva sua experiência profissional"
          />
        </div>

        <div className="input-group">
          <label>Formação Acadêmica:</label>
          <textarea
            value={formacao}
            onChange={(e) => setFormacao(e.target.value)}
            required
            placeholder="Descreva sua formação acadêmica"
          />
        </div>

        <button type="submit">Enviar Currículo</button>
      </form>
    </div>
  );
}

export default Curriculo;
