import React, { useState } from 'react';

function Curriculo() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [formacao, setFormacao] = useState('');
  const [experiencia, setExperiencia] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`
      Currículo enviado:
      Nome: ${nome}
      E-mail: ${email}
      Formação: ${formacao}
      Experiência Profissional: ${experiencia}
    `);
  };

  return (
    <div id="curriculo" className="curriculo-container">
      <h2>Cadastro de Currículo</h2>
      <form onSubmit={handleSubmit}>
        {/* Nome */}
        <div className="input-group">
          <label>Nome Completo:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            placeholder="Digite seu nome completo"
          />
        </div>

        {/* E-mail */}
        <div className="input-group">
          <label>E-mail:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Digite seu e-mail"
          />
        </div>

        {/* Formação */}
        <div className="input-group">
          <label>Formação:</label>
          <select
            value={formacao}
            onChange={(e) => setFormacao(e.target.value)}
            required
          >
            <option value="">Selecione sua formação</option>
            <option value="Graduação - Ciência da Computação">Graduação - Ciência da Computação</option>
            <option value="Pós-Graduação - Banco de Dados">Pós-Graduação - Banco de Dados</option>
            <option value="Técnico - Eletrônica Básica">Técnico - Eletrônica Básica</option>
          </select>
        </div>

        {/* Experiência Profissional */}
        <div className="input-group">
          <label>Experiência Profissional:</label>
          <select
            value={experiencia}
            onChange={(e) => setExperiencia(e.target.value)}
            required
          >
            <option value="">Selecione sua experiência</option>
            <option value="Desenvolvedor Frontend - Empresa A">Desenvolvedor Frontend - Empresa A</option>
            <option value="Desenvolvedor Backend - Empresa B">Desenvolvedor Backend - Empresa B</option>
            <option value="Analista de Sistemas - Empresa C">Analista de Sistemas - Empresa C</option>
          </select>
        </div>

        <button type="submit">Enviar Currículo</button>
      </form>
    </div>
  );
}

export default Curriculo;
