// src/components/FormationForm.js
import React, { useState } from 'react';
import { Form, Button, Card, ListGroup } from 'react-bootstrap';

function FormationForm() {
  const [formacao, setFormacao] = useState({
    tipo: '',
    graduacao: '',
    graduacaoTipo: '',
    posGraduacao: [],
    tecnico: ''
  });

  const [novoPos, setNovoPos] = useState('');

  const handleTipoChange = (e) => {
    setFormacao({ ...formacao, tipo: e.target.value });
  };

  const handleGraduacaoChange = (e) => {
    setFormacao({ ...formacao, graduacao: e.target.value });
  };

  const handleGraduacaoTipoChange = (e) => {
    setFormacao({ ...formacao, graduacaoTipo: e.target.value });
  };

  const handlePosGraduacaoChange = () => {
    setFormacao({
      ...formacao,
      posGraduacao: [...formacao.posGraduacao, novoPos]
    });
    setNovoPos('');
  };

  const handleTecnicoChange = (e) => {
    setFormacao({ ...formacao, tecnico: e.target.value });
  };

  return (
    <div className="mt-5 pt-5">
      <Card>
        <Card.Header as="h5">Preencha os Dados de Formação</Card.Header>
        <Card.Body>
          {/* Tipo de Formação */}
          <Form.Group controlId="formTipo">
            <Form.Label>Tipo de Formação</Form.Label>
            <Form.Control as="select" value={formacao.tipo} onChange={handleTipoChange}>
              <option value="">Selecione</option>
              <option value="graduacao">Graduação</option>
              <option value="posGraduacao">Pós-Graduação</option>
              <option value="tecnico">Técnico</option>
            </Form.Control>
          </Form.Group>

          {/* Graduação */}
          {formacao.tipo === 'graduacao' && (
            <>
              <Form.Group controlId="formGraduacao">
                <Form.Label>Graduação</Form.Label>
                <Form.Control
                  type="text"
                  value={formacao.graduacao}
                  onChange={handleGraduacaoChange}
                  placeholder="Digite o curso"
                />
              </Form.Group>
              <Form.Group controlId="formGraduacaoTipo">
                <Form.Label>Tipo de Graduação</Form.Label>
                <Form.Control
                  as="select"
                  value={formacao.graduacaoTipo}
                  onChange={handleGraduacaoTipoChange}
                >
                  <option value="">Selecione</option>
                  <option value="Bacharelado">Bacharelado</option>
                  <option value="Licenciatura">Licenciatura</option>
                  <option value="Tecnólogo">Tecnólogo</option>
                </Form.Control>
              </Form.Group>
            </>
          )}

          {/* Pós-Graduação */}
          {formacao.tipo === 'posGraduacao' && (
            <>
              <Form.Group controlId="formPosGraduacao">
                <Form.Label>Pós-Graduação</Form.Label>
                <Form.Control
                  type="text"
                  value={novoPos}
                  onChange={(e) => setNovoPos(e.target.value)}
                  placeholder="Digite o curso de Pós-Graduação"
                />
              </Form.Group>
              <Button onClick={handlePosGraduacaoChange}>Adicionar Pós-Graduação</Button>
            </>
          )}

          {/* Técnico */}
          {formacao.tipo === 'tecnico' && (
            <Form.Group controlId="formTecnico">
              <Form.Label>Técnico</Form.Label>
              <Form.Control
                type="text"
                value={formacao.tecnico}
                onChange={handleTecnicoChange}
                placeholder="Digite o curso técnico"
              />
            </Form.Group>
          )}

          <Button variant="primary" className="mt-3">Salvar Formação</Button>
        </Card.Body>
      </Card>

      {/* Exibição da Formação Selecionada */}
      {formacao.tipo && (
        <Card className="mt-3">
          <Card.Header as="h5">Formação Selecionada</Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <strong>Tipo de Formação:</strong> {formacao.tipo}
            </ListGroup.Item>
            {formacao.tipo === 'graduacao' && (
              <>
                <ListGroup.Item>
                  <strong>Curso:</strong> {formacao.graduacao}
                </ListGroup.Item>
                <ListGroup.Item>
                  <strong>Tipo de Graduação:</strong> {formacao.graduacaoTipo}
                </ListGroup.Item>
              </>
            )}
            {formacao.tipo === 'posGraduacao' && formacao.posGraduacao.length > 0 && (
              <ListGroup.Item>
                <strong>Pós-Graduação:</strong>
                <ul>
                  {formacao.posGraduacao.map((item, index) => (
                    <li key={index}>{item}</li>
                  ))}
                </ul>
              </ListGroup.Item>
            )}
            {formacao.tipo === 'tecnico' && (
              <ListGroup.Item>
                <strong>Curso Técnico:</strong> {formacao.tecnico}
              </ListGroup.Item>
            )}
          </ListGroup>
        </Card>
      )}
    </div>
  );
}

export default FormationForm;
