import React, { useState } from 'react';
import { Card, ListGroup, Form, Button } from 'react-bootstrap';
import axios from 'axios';

function Curriculo() {
  // Estados para armazenar as informações do usuário
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [cep, setCep] = useState('');
  const [endereco, setEndereco] = useState('');
  const [bairro, setBairro] = useState('');
  const [cidade, setCidade] = useState('');
  const [estado, setEstado] = useState('');
  const [tipoFormacao, setTipoFormacao] = useState('');
  const [tipoGraduacao, setTipoGraduacao] = useState('');
  const [additionalInfo, setAdditionalInfo] = useState('');
  const [empresa, setEmpresa] = useState('');
  const [cargo, setCargo] = useState('');
  const [anoInicio, setAnoInicio] = useState('');
  const [anoFim, setAnoFim] = useState('');

  const [experiencias, setExperiencias] = useState([]);

  
  const handleAddExperience = () => {

    const novaExperiencia = {
      empresa,
      cargo,
      anoInicio,
      anoFim,
    };


    setExperiencias([...experiencias, novaExperiencia]);

    // Limpa os campos após adicionar
    setEmpresa('');
    setCargo('');
    setAnoInicio('');
    setAnoFim('');
  };


  const handleAdditionalInfoChange = (event) => {
    setAdditionalInfo(event.target.value);
  };


  const buscarEndereco = async () => {
    try {
      const response = await axios.get(`https://viacep.com.br/ws/${cep}/json/`);
      const {bairro, cidade, uf} = response.data;


      setBairro(bairro);
      setCidade(cidade);
      setEstado(uf);

    } catch (error) {
      alert("CEP não encontrado ou inválido.");
    }
  };

  return (
    <div className="mt-5 pt-5">
      <Card>
        <Card.Header as="h5">IDENTIFICAÇÃO</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <strong>Nome:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu nome"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Telefone:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu telefone(Confirme abaixo se é WhatsApp)"
              value={telefone}
              onChange={(e) => setTelefone(e.target.value)}
            />
            <a href={`https://wa.me/${telefone}`} target="_blank" rel="noopener noreferrer">WhatsApp</a>
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>CEP:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu CEP"
              value={cep}
              onChange={(e) => setCep(e.target.value)}
            />
            <Button variant="primary" onClick={buscarEndereco}>Buscar Endereço</Button>
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Endereço:</strong>
            <Form.Control
              type="text"
              placeholder="Endereço"
              value={endereco}
              onChange={(e) => setEndereco(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Bairro:</strong>
            <Form.Control
              type="text"
              placeholder="Bairro"
              value={bairro}
              onChange={(e) => setBairro(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Cidade:</strong>
            <Form.Control
              type="text"
              placeholder="Cidade"
              value={cidade}
              onChange={(e) => setCidade(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Estado:</strong>
            <Form.Control
              type="text"
              placeholder="Estado"
              value={estado}
              onChange={(e) => setEstado(e.target.value)}
            />
          </ListGroup.Item>
        </ListGroup>

        <Card.Header as="h5">FORMAÇÃO</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <strong>Tipo de Formação:</strong>
            <Form.Control as="select" onChange={(e) => setTipoFormacao(e.target.value)} value={tipoFormacao}>
              <option value="">Selecione</option>
              <option value="Tecnico">Técnico</option>
              <option value="Graduacao">Graduação</option>
              <option value="PosGraduacao">Pós-Graduação</option>
              <option value="PosGraduacao">MBA</option>
              <option value="PosGraduacao">Mestrado</option>
              <option value="PosGraduacao">Doutorado</option>
              <option value="PosGraduacao">Pós-Doutorado</option>

            </Form.Control>
          </ListGroup.Item>

          {tipoFormacao === "Graduacao" && (
            <ListGroup.Item>
              <strong>Tipo de Graduação:</strong>
              <Form.Control as="select" onChange={(e) => setTipoGraduacao(e.target.value)} value={tipoGraduacao}>
                <option value="">Selecione</option>
                <option value="Bacharelado">Bacharelado</option>
                <option value="Licenciatura">Licenciatura</option>
                <option value="Tecnologo">Tecnólogo</option>
              </Form.Control>
            </ListGroup.Item>
          )}
        </ListGroup>

        <Card.Header as="h5">EXPERIÊNCIA</Card.Header>
        <ListGroup variant="flush">

          <ListGroup.Item>
            <strong>Empresa:</strong>
            <Form.Control
              type="text"
              placeholder="Digite o nome da empresa"
              value={empresa}
              onChange={(e) => setEmpresa(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Cargo:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu cargo"
              value={cargo}
              onChange={(e) => setCargo(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Ano de Início:</strong>
            <Form.Control
              type="number"
              placeholder="Digite o ano de início"
              value={anoInicio}
              onChange={(e) => setAnoInicio(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Ano de Término:</strong>
            <Form.Control
              type="number"
              placeholder="Digite o ano de término (se aplicável)"
              value={anoFim}
              onChange={(e) => setAnoFim(e.target.value)}
            />
          </ListGroup.Item>

          <Button variant="primary" onClick={handleAddExperience}>
            Adicionar mais Experiências
          </Button>
        </ListGroup>


        <Card.Header as="h5">Experiências Profissionais</Card.Header>
        <ListGroup variant="flush">
          {experiencias.map((exp, index) => (
            <ListGroup.Item key={index}>
              <strong>Empresa:</strong> {exp.empresa} – {exp.anoInicio} a {exp.anoFim ? exp.anoFim : 'atual'}
              <br />
              <strong>Cargo:</strong> {exp.cargo}
              <Form.Control
              type="text"
              placeholder="Digite o nome da empresa"
              value={empresa}
              onChange={(e) => setEmpresa(e.target.value)}
            />
            </ListGroup.Item>
          ))}
        </ListGroup>


        <Card.Header as="h5">Conte-nos mais sobre você:</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <Form.Control
              as="textarea"
              rows={3}
              value={additionalInfo}
              onChange={handleAdditionalInfoChange}
              placeholder="Compartilhe algo interessante sobre você..."
            />
          </ListGroup.Item>
        </ListGroup>
      </Card>
    </div>
  );
}

export default Curriculo;
