import React, { useState } from 'react';
import { Card, ListGroup, Form, Button } from 'react-bootstrap';
import axios from 'axios';

function Curriculo() {
  // Estados para armazenar as informações do usuário
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [cep, setCep] = useState('');
  const [endereco, setEndereco] = useState('');
  const [bairro, setBairro] = useState('');
  const [cidade, setCidade] = useState('');
  const [estado, setEstado] = useState('');
  const [additionalInfo, setAdditionalInfo] = useState('');

  // Função para atualizar o texto adicional
  const handleAdditionalInfoChange = (event) => {
    setAdditionalInfo(event.target.value);
  };

  // Função para buscar o endereço no ViaCEP
  const buscarEndereco = async () => {
    try {
      const response = await axios.get(`https://viacep.com.br/ws/${cep}/json/`);
      const { logradouro, bairro, localidade, estado } = response.data;

      setEndereco(logradouro);
      setBairro(bairro);
      setCidade(localidade);
      setEstado(estado);
    } catch (error) {
      alert("CEP não encontrado ou inválido.");
    }
  };

  return (
    <div className="mt-5 pt-5">
      <Card>
        <Card.Header as="h5">IDENTIFICAÇÃO</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <strong>Nome:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu nome"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Telefone:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu telefone"
              value={telefone}
              onChange={(e) => setTelefone(e.target.value)}
            />
            <a href={`https://wa.me/${telefone}`} target="_blank" rel="noopener noreferrer">WhatsApp</a>
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>CEP:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu CEP"
              value={cep}
              onChange={(e) => setCep(e.target.value)}
            />
            <Button variant="primary" onClick={buscarEndereco}>Buscar Endereço</Button>
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Endereço:</strong>
            <Form.Control
              type="text"
              placeholder="Endereço"
              value={endereco}
              onChange={(e) => setEndereco(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Bairro:</strong>
            <Form.Control
              type="text"
              placeholder="Bairro"
              value={bairro}
              onChange={(e) => setBairro(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Cidade:</strong>
            <Form.Control
              type="text"
              placeholder="Cidade"
              value={cidade}
              onChange={(e) => setCidade(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Estado:</strong>
            <Form.Control
              type="text"
              placeholder="Estado"
              value={estado}
              onChange={(e) => setEstado(e.target.value)}
            />
          </ListGroup.Item>
        </ListGroup>

        <Card.Header as="h5">FORMAÇÃO</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <strong>Tipo de Formação:</strong>
            <Form.Control as="select">
              <option value="">Selecione</option>
              <option value="Graduacao">Graduação</option>
              <option value="PosGraduacao">Pós-Graduação</option>
              <option value="Tecnico">Técnico</option>
            </Form.Control>
          </ListGroup.Item>
        </ListGroup>

        <Card.Header as="h5">EXPERIÊNCIA</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>Empresa B – 04/2024 a atual</ListGroup.Item>
          <ListGroup.Item>Empresa A – 01/2022 a 03/2024</ListGroup.Item>
        </ListGroup>

        {/* INFORMAÇÕES ADICIONAIS */}
        <Card.Header as="h5">INFORMAÇÕES ADICIONAIS</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item><a href="https://github.com/eu" target="_blank" rel="noopener noreferrer">Github</a></ListGroup.Item>
          <ListGroup.Item><a href="https://linkedin.com/in/eu" target="_blank" rel="noopener noreferrer">LinkedIn</a></ListGroup.Item>
        </ListGroup>

        {/* Campo para o usuário adicionar informações adicionais */}
        <Card.Header as="h5">Conte-nos mais sobre você:</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <Form.Control
              as="textarea"
              rows={3}
              value={additionalInfo}
              onChange={handleAdditionalInfoChange}
              placeholder="Compartilhe algo interessante sobre você..."
            />
          </ListGroup.Item>
        </ListGroup>
      </Card>
    </div>
  );
}

export default Curriculo;
