import React, { useState } from 'react';
import { Card, ListGroup, Form } from 'react-bootstrap';

function Curriculo() {
  // Estado para armazenar as informações dos campos
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [endereco, setEndereco] = useState('');

  return (
    <div className="mt-5 pt-5">
      <Card>
        <Card.Header as="h5">IDENTIFICAÇÃO</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <strong>Nome:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu nome"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
            />
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Telefone:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu telefone"
              value={telefone}
              onChange={(e) => setTelefone(e.target.value)}
            />
            <a href={`https://wa.me/${telefone}`} target="_blank" rel="noopener noreferrer">WhatsApp</a>
          </ListGroup.Item>
          <ListGroup.Item>
            <strong>Endereço:</strong>
            <Form.Control
              type="text"
              placeholder="Digite seu endereço"
              value={endereco}
              onChange={(e) => setEndereco(e.target.value)}
            />
          </ListGroup.Item>
        </ListGroup>

        <Card.Header as="h5">FORMAÇÃO</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <strong>Tipo de Formação:</strong>
            <Form.Control as="select">
              <option value="">Selecione</option>
              <option value="Graduacao">Graduação</option>
              <option value="PosGraduacao">Pós-Graduação</option>
              <option value="Tecnico">Técnico</option>
            </Form.Control>
          </ListGroup.Item>
        </ListGroup>
      </Card>
      
    </div>
  );
}

export default Curriculo;
