import React, { useState } from "react";

function Curriculo() {
  // Estados para armazenar informações
  const [email, setEmail] = useState("");
  const [cep, setCep] = useState(""); // Estado para armazenar o CEP
  const [endereco, setEndereco] = useState({
    logradouro: "",
    bairro: "",
    cidade: "",
    estado: "",
  });

  // Função para buscar o endereço via CEP
  const buscarEndereco = async () => {
    if (cep.length === 8) { // Verifica se o CEP tem 8 caracteres
      try {
        const response = await fetch(`https://viacep.com.br/ws/${cep}/json/`);
        const data = await response.json();
        if (!data.erro) {
          setEndereco({
            logradouro: data.logradouro || "",
            bairro: data.bairro || "",
            cidade: data.localidade || "",
            estado: data.uf || "",
          });
        } else {
          alert("CEP não encontrado");
        }
      } catch (error) {
        alert("Erro ao buscar o CEP");
      }
    }
  };

  return (
    <div id="curriculo" className="container">
      <form>
        {/* Campo de email */}
        <div className="input-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Digite seu email"
          />
        </div>

        {/* Novo campo para CEP */}
        <div className="input-group">
          <label>CEP:</label>
          <input
            type="text"
            value={cep}
            onChange={(e) => setCep(e.target.value)}
            maxLength="8"
            placeholder="Digite seu CEP"
            onBlur={buscarEndereco} // Chama a função ao sair do campo
          />
        </div>

        {/* Campos de Endereço */}
        <div className="input-group">
          <label>Endereço:</label>
          <input
            type="text"
            value={endereco.logradouro}
            readOnly
            placeholder="Logradouro"
          />
        </div>

        <div className="input-group">
          <label>Bairro:</label>
          <input
            type="text"
            value={endereco.bairro}
            readOnly
            placeholder="Bairro"
          />
        </div>

        <div className="input-group">
          <label>Cidade:</label>
          <input
            type="text"
            value={endereco.cidade}
            readOnly
            placeholder="Cidade"
          />
        </div>

        <div className="input-group">
          <label>Estado:</label>
          <input
            type="text"
            value={endereco.estado}
            readOnly
            placeholder="Estado"
          />
        </div>

        {/* Botão para enviar */}
        <button type="submit">Enviar Currículo</button>
      </form>
    </div>
  );
}

export default Curriculo;
