import React, { useState } from "react";

function Curriculo() {
  // Estados para armazenar informações
  const [email, setEmail] = useState("");
  const [cep, setCep] = useState(""); // Estado para armazenar o CEP
  const [logradouro, setLogradouro] = useState(""); // Estado para o logradouro
  const [bairro, setBairro] = useState(""); // Estado para o bairro
  const [cidade, setCidade] = useState(""); // Estado para a cidade
  const [estado, setEstado] = useState(""); // Estado para o estado

  return (
    <div id="curriculo" className="container">
      <form>
        {/* Campo de email */}
        <div className="input-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Digite seu email"
          />
        </div>

        {/* Campos de Endereço */}
        <div className="input-group">
          <label>CEP:</label>
          <input
            type="text"
            value={cep}
            onChange={(e) => setCep(e.target.value)}
            maxLength="8"
            placeholder="Digite seu CEP"
          />
        </div>

        <div className="input-group">
          <label>Logradouro:</label>
          <input
            type="text"
            value={logradouro}
            onChange={(e) => setLogradouro(e.target.value)}
            placeholder="Digite seu logradouro"
          />
        </div>

        <div className="input-group">
          <label>Bairro:</label>
          <input
            type="text"
            value={bairro}
            onChange={(e) => setBairro(e.target.value)}
            placeholder="Digite seu bairro"
          />
        </div>

        <div className="input-group">
          <label>Cidade:</label>
          <input
            type="text"
            value={cidade}
            onChange={(e) => setCidade(e.target.value)}
            placeholder="Digite sua cidade"
          />
        </div>

        <div className="input-group">
          <label>Estado:</label>
          <input
            type="text"
            value={estado}
            onChange={(e) => setEstado(e.target.value)}
            placeholder="Digite seu estado"
          />
        </div>

        {/* Botão para enviar */}
        <button type="submit">Enviar Currículo</button>
      </form>
    </div>
  );
}

export default Curriculo;
